import React, { Component } from 'react';
import { CopyToClipboard } from 'react-copy-to-clipboard';
import { Link } from 'react-router-dom';
import  chroma from 'chroma-js';
import { withStyles } from '@material-ui/styles';


import './ColorBox.css';

const styles = {
    colorBox: {
        width: "20%",
        height: "25%",
        margin: "0 auto",
        display:"inline-block",
        position: "relative",
        cursor: "pointer",
        marginBottom: "-3.5px"
    },
    copyText: {
        color: props => 
            chroma(props.background).luminance() >=0.7 ? "black" : "white" 
    },
    colorName: {
        color: props => 
            chroma(props.background).luminance() <=0.08 ? "white" : "black"
    },
    seeMore: {
        color: props => chroma(props.background).luminance() >= 0.7 ? "rgba(0, 0, 0, 0.6)" : "white",
        background: "rgba(255, 255, 255, 0.3)",
        position: "absolute",
        border: "none",
        right: "0px",
        bottom: "0px",
        width: "60px",
        height: "30px",
        textAlign: "center",
        lineHeight: "30px",
        textTransform: "uppercase",
    },
    copyButton: {
        color: props => chroma(props.background).luminance() >= 0.7 ? "rgba(0, 0, 0, 0.6)" : "white",
        width: "100px",
        height: "30px",
        position: "absolute",
        display: "inline-block",
        top: "50%",
        textAlign: "center",
        left: "50%",
        marginTop: "-15px",
        marginLeft: "-50px",
        outline: "none",
        background: "rgba(255, 255, 255, 0.3)",
        fontSize: "1rem",
        lineHeight: "30px",
        textTransform: "uppercase",
        border: "none",
        textDecoration: "none"
    }
}


class ColorBox extends Component {
    constructor(props){
        super(props);
        this.state = { copied: false };
        this.chageCopyState = this.chageCopyState.bind(this);
    }
    chageCopyState(){
        this.setState({ copied: true }, () => {
            setTimeout(() => this.setState({ copied: false }), 1000);
        });
    }

    render () {
        const { name, background, moreUrl, showLink, classes } = this.props;
        const { copied } = this.state;
        const isDarkColor = chroma(background).luminance() <= 0.08;
        const isLightColor = chroma(background).luminance() >= 0.7;
        return (
            <CopyToClipboard text={background} onCopy={this.chageCopyState}>
                <div style={{ background }} className={classes.colorBox}>
                    <div 
                        style={{ background }} 
                        className={`copy-overlay ${copied && "show"}`} 
                    />
                    <div className={`copy-msg ${copied && "show"}`}>
                        <h1>Copied!</h1>
                        <p className={classes.copyText}>{background}</p>
                    </div>
                    <div className="copy-container">
                        <div className="box-content">
                            <span className={classes.colorName}>{name}</span>
                        </div>
                        <button className={classes.copyButton} >
                            Copy
                        </button>
                    </div>
                    {showLink && (
                        <Link to={moreUrl} onClick={e => e.stopPropagation()}>
                            <span className={classes.seeMore}>More</span>
                        </Link>
                    )}
                </div>
            </CopyToClipboard>
        );
    }
}

export default withStyles(styles)(ColorBox);
